create table BIEN
(
    IDENTIFICADORBIEN2 int identity
        constraint PK_BIEN
            primary key nonclustered,
    RUC                char(13)     not null
        constraint FK_BIEN_TIENE_PROVEEDO
            references PROVEEDOR,
    NOMBREBIEN         varchar(100) not null,
    PRECIOBIEN         float        not null,
    TIPOBIEN           varchar(100),
    MARCA              varchar(30),
    CATEGORIA          varchar(30)
)
