create table OBSERVACIONES
(
    IDOBS           int not null
        constraint PK_OBSERVACIONES
            primary key nonclustered,
    IDEMP           int
        constraint FK_OBSERVAC_RELATIONS_EMPLEADO
            references EMPLEADO,
    TIPOOBS         varchar(1024),
